targets.expr:
count(
    label_replace(
        kube_pod_info{namespace=~"$namespace",created_by_kind=~"$workload_type", created_by_name=~"$workload_name"} - 0, "node_ip", "$1", "host_ip", "(.*)"
    ) * on (node_ip) group_left(label_eks_amazonaws_com_nodegroup)
    label_replace( 
        kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
    )
)by(created_by_name, node_ip)

count(
    label_replace(
        kube_pod_info{namespace=~"$namespace",created_by_kind=~"$workload_type", created_by_name=~"$workload_name"} - 0, "node_ip", "$1", "host_ip", "(.*)"
    ) * on (node_ip) group_left(label_eks_amazonaws_com_nodegroup)
    label_replace( 
        kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
    )
)by(created_by_name, node_ip)
/ on (created_by_name) group_left()
( count(kube_pod_info{created_by_kind!="DaemonSet"})by(created_by_name) > 1 )

label_replace(
    kube_pod_info{namespace=~"$namespace",created_by_kind=~"$workload_type", created_by_name=~"$workload_name"} - 0, "node_ip", "$1", "host_ip", "(.*)"
) * on (node_ip) group_left(label_eks_amazonaws_com_nodegroup)
label_replace( 
    kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
) * on (created_by_name) group_left() count(kube_pod_info)by(created_by_name)

label_replace(
    kube_pod_info{namespace=~"$namespace",created_by_kind=~"$workload_type", created_by_name=~"$workload_name"} - 0, "node_ip", "$1", "host_ip", "(.*)"
) * on (node_ip) group_left(label_eks_amazonaws_com_nodegroup)
label_replace( 
    kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
) * on (created_by_name) group_left() count(kube_pod_info)by(created_by_name)

label_replace(
    node_uname_info - 0, "node_ip", "$1", "instance", "(.*):(.*)"
) * on (node_ip) group_left(label_eks_amazonaws_com_nodegroup, label_kubernetes_io_hostname)
label_replace( 
    kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
)

label_replace(
  sum(time() - node_boot_time_seconds)by(instance), "node_ip", "$1", "instance", "(.*):(.*)"
) * on (node_ip)
label_replace( 
  kube_node_labels{node=~"$node"}, "node_ip", "$1.$2.$3.$4", "node", "ip-(.*)-(.*)-(.*)-(.*).ap.*"
)

(
    sum(kube_pod_container_resource_requests{resource="cpu"}) by (node)
    / on (node)
    kube_node_status_allocatable{resource="cpu"} 
) * on (node) group_left(label_eks_amazonaws_com_nodegroup, node_select)
label_replace( 
    kube_node_labels{label_eks_amazonaws_com_nodegroup=~"$nodegroup", node=~"$node"}, "node_select", "$1", "node", "ip-(.*).ap.*"
)

(
    sum(kube_pod_container_resource_requests{resource="memory"}) by (node)
    / on (node)
    kube_node_status_allocatable{resource="memory"} 
) * on (node) group_left(label_eks_amazonaws_com_nodegroup, node_select)
label_replace( 
    kube_node_labels{label_eks_amazonaws_com_nodegroup=~"$nodegroup", node=~"$node"}, "node_select", "$1", "node", "ip-(.*).ap.*"
)

label_replace(
    sum(container_memory_working_set_bytes{container!="", pod!=""}) by(node), "node_select", "$1", "node", "ip-(.*).ap.*"
) * on (node_select) group_left(label_eks_amazonaws_com_nodegroup)
label_replace( 
    kube_node_labels{node=~"$node"}, "node_select", "$1", "node", "ip-(.*).ap.*"
)
